// JavaScript Document

//Automatic read more

var thumbnail_mode = "yes";; //yes -with thumbnail, no -no thumbnail

summary_noimg = 350; //summary length when no image
summary_img = 150; //summary length when with image

function removeHtmlTag(strx, chop) {
    if (strx.indexOf("<") != -1) {
        var s = strx.split("<");
        for (var i = 0; i < s.length; i++) {
            if (s[i].indexOf(">") != -1) {
                s[i] = s[i].substring(s[i].indexOf(">") + 1, s[i].length)
            }
        }
        strx = s.join("")
    }
    chop = (chop < strx.length - 1) ? chop : strx.length - 2;
    while (strx.charAt(chop - 1) != ' ' && strx.indexOf(' ', chop) != -1) chop++;
    strx = strx.substring(0, chop - 1);
    return strx + '...'
}

function rm(a, b, c, u) {
    var p = document.getElementById(a);
    var q = document.getElementById(b);
    var r = document.getElementById(c);
    var imgtag = "";
    var img = p.getElementsByTagName("img");
    var summ = summary_noimg;
    if (thumbnail_mode == "yes") {
        if (img.length >= 1) {
            imgtag = "<a rel='prettyPhoto' href=" + img[0].src + "><img src=" + img[0].src + " style='width:100%;' /></a>";
            summ = summary_img
        }
    }
    var summary = '<p>' + removeHtmlTag(p.innerHTML, summ) + '</p>';
    q.innerHTML = imgtag;
    r.innerHTML = summary;
    p.parentNode.removeChild(p)
}
