$(document).ready(function() {
    $('.post-body img:eq(0)').each(function() {
        var image = $(this);
        var src = image.attr("src");
        var href = image.parent().attr("href");
        var imgtag = $('<a rel="prettyPhoto" href="' + href + '"><img src="' + src + '" /></a>');
        imgtag.appendTo(".bckpic");
        $(this).parent().remove()
    })
});  

