            m = p,
            o = this.id;
        if (l.value == "") {
            if (l.type == "password") {
                if (!p.data("placeholder-textinput")) {
                    try {
                        q = p.clone().attr({
                            type: "text"
                        })
                    } catch (n) {
                        q = $("<input>").attr($.extend(g(this), {
                            type: "text"
                        }))
                    }
                    q.removeAttr("name").data({
                        "placeholder-password": true,
                        "placeholder-id": o
                    }).bind("focus.placeholder", b);
                    p.data({
                        "placeholder-textinput": q,
                        "placeholder-id": o
                    }).before(q)
                }
                p = p.removeAttr("id").hide().prev().attr("id", o).show()
            }
            p.addClass("placeholder");
            p[0].value = p.attr("placeholder")
        } else p.removeClass("placeholder")
    }
})(this, document, jQuery);
var $j = jQuery.noConflict();
$j(document).ready(function() {
    $j(".flexslider").flexslider({
        autoPlay: true,
        pauseOnAction: false,
        animation: "fade",
        start: function(a) {
            $j(".caption_wrapper").animate({
                "bottom": "50px",
                "opacity": 1
            }, 500)
        },
        before: function(a) {
            $j(".caption_wrapper").animate({
                "bottom": "0px",
                "opacity": 0
            }, 500)
        },
        after: function(a) {
            $j(".caption_wrapper").animate({
                "bottom": "50px",
                "opacity": 1
            }, 500)
        }
    });
    set_slider_height();
    $j(window).resize(function() {
        set_slider_height()
    });

    function set_slider_height() {
        var a = parseInt($j("#homeslider ul.slides").find("li").attr("data-height"));
        var b;
        if ($j(window).width() < 1030) b = a * $j(window).width() / 1030;
        else b = a;
        $j("#homeslider ul.slides").find("li").height(b)
    }
    var c = $j("#post_grids");
    c.imagesLoaded(function() {
        c.masonry({
            itemSelector: ".post_col"
        })
    });
    $j("#post_grids").masonry({
        itemSelector: ".post_col",
        gutterWidth: 0,
        columnWidth: function(a) {
            return a / 3
        }
    });
    $j("#Header1_headerimg").load(function() {
        var a = $j(this).height();
        var b = a / 2 - 13;
        $j(".socials").css("padding-top", b)
    });
    $j("input, textarea").placeholder();
    $j("ul.sf-menu").superfish({
        animation: {
            opacity: "show"
        },
        speed: 200,
        delay: 10,
        animation: {
            opacity: "show",
            height: "show"
        }
    });
    $j("#top_menu ul.sf-menu").mobileMenu({
        defaultText: "Navigation ...",
        className: "select_menu",
        subMenuDash: "&ndash;"
    });
    $j("select.select_menu").each(function() {
        var a = $j(this).attr("title");
        if ($j("option:selected", this).val() != "") a = jQuery("option:selected", this).text();
        $j(this).css({
            "z-index": 10,
            "opacity": 0,
            "-khtml-appearance": "none"
        }).after('<span class="nav_select">' + a + '<span class="menu_icon_wrapper"><span class="menu_icon"></span></span></span>').change(function() {
            val = $jj("option:selected", this).text();
            $j(this).next().text(val)
        })
    });
    $j(".search_btn").click(function() {
        $j(this).toggleClass("close");
        $j(".search_box").toggleClass("show");
        $j(".sf-menu").toggleClass("hide")
    })
});
