function createSummaryAndThumb(pID, title, url, date, comment) {
    var posturl = url;
    var title = title;
    var date = date;
    var comment = comment;
    var div = document.getElementById(pID);
    var content = div.innerHTML;
    if (/<!--\s*more\s*-->/.test(content)) {
        div.innerHTML = getSummaryLikeWP(pID);
        div.style.display = "block";
    } else {
        var imgtag = "";
        var img = div.getElementsByTagName("img");
        var summ = summary_noimg;
        if (img.length >= 1) {
            imgtag = '<div class="post-thumb"><a title="' + title + '" href="' + posturl + '"><img width="100%" height="230px" class="balsam" src="' + img[0].src + '"/></a></div>';
            summ = summary_img;
        }
        var summary = '<div class="topwrap">' + imgtag + '<div class="wrapbg">' + '</div>';
        div.innerHTML = summary;
    }
}

