            d = s["substr"](b + 5, c - b - 5);
            if ((a != -1) && (b != -1) && (c != -1) && (d != "")) {
                thumburl[relatedTitlesNum] = d
            } else {
                thumburl[relatedTitlesNum] = "http://3.bp.blogspot.com/-yL7ud4NgOMk/UfRYF2NDTSI/AAAAAAAAByE/IKH6O4gr9rM/s1600/no+image.jpg"
            }
        };
        if (relatedTitles[relatedTitlesNum]["length"] > 35) {
            relatedTitles[relatedTitlesNum] = relatedTitles[relatedTitlesNum]["substring"](0, 35) + "..."
        };
        for (var k = 0; k < entry["link"]["length"]; k++) {
            if (entry["link"][k]["rel"] == "alternate") {
                relatedUrls[relatedTitlesNum] = entry["link"][k]["href"];
                relatedTitlesNum++
            }
        }
    }
};

function removeRelatedDuplicates_thumbs() {
    var tmp = new Array(0);
    var tmp2 = new Array(0);
    var tmp3 = new Array(0);
    for (var i = 0; i < relatedUrls["length"]; i++) {
        if (!contains_thumbs(tmp, relatedUrls[i])) {
            tmp["length"] += 1;
            tmp[tmp["length"] - 1] = relatedUrls[i];
            tmp2["length"] += 1;
            tmp3["length"] += 1;
            tmp2[tmp2["length"] - 1] = relatedTitles[i];
            tmp3[tmp3["length"] - 1] = thumburl[i]
        }
    };
    relatedTitles = tmp2;
    relatedUrls = tmp;
    thumburl = tmp3
};

function contains_thumbs(a, e) {
    for (var j = 0; j < a["length"]; j++) {
        if (a[j] == e) {
            return true
        }
    };
    return false
};

function printRelatedLabels_thumbs() {
    for (var i = 0; i < relatedUrls["length"]; i++) {
        if ((relatedUrls[i] == currentposturl) || (!(relatedTitles[i]))) {
            relatedUrls["splice"](i, 1);
            relatedTitles["splice"](i, 1);
            thumburl["splice"](i, 1);
            i--
        }
    };
    var r = Math["floor"]((relatedTitles["length"] - 1) * Math["random"]());
    var i = 0;
    if (relatedTitles["length"] > 0) {
        document["write"]("<h3>" + relatedpoststitle + "</h3>")
    };
    document["write"]("<div style=\"clear: both;\"/>");
    while (i < relatedTitles["length"] && i < 20 && i < maxresults) {
        document["write"]("<a class=\"dealz");
        if (i != 0) {
            document["write"]("\"")
        } else {
            document["write"]("\"")
        };
        document["write"](" href=\"" + relatedUrls[r] + "\"><div class=\"arrow-but\"><img class=\"mugs-in\" src=\"" + thumburl[r] + "\"/><div class=\"delate-post\">" + relatedTitles[r] + "</div></div></a>");
        if (r < relatedTitles["length"] - 1) {
            r++
        } else {
            r = 0
        };
        i++
    };
    document["write"]("</div>");
    relatedUrls["splice"](0, relatedUrls["length"]);
    thumburl["splice"](0, thumburl["length"]);
    relatedTitles["splice"](0, relatedTitles["length"])
};
