body.script:
//

  $(function() {
    var top = $("#to-top");
    var scrollTop;
    if (top.length) {
      var scrollTrigger = 200, // px
          backToTop = function () {
            scrollTop = $(window).scrollTop();
            if (scrollTop > scrollTrigger) {
              top.addClass('fadeIn');
            } else {
              top.removeClass('fadeIn');
            }
          };
      backToTop();
      $(window).on('scroll', function () {
        backToTop();
      });
      top.on('click', function (e) {
        e.preventDefault();
        $('html,body').animate({
          scrollTop: 0
        }, 600);
      });
    }
  });

