        } catch (p) {
            s = i.content.$t, a = s.indexOf("<img"), b = s.indexOf('src="', a), c = s.indexOf('"', b + 5), d = s.substr(b + 5, c - b - 5), u = -1 != a && -1 != b && -1 != c && "" != d ? d : "http://1.bp.blogspot.com/-pmtmCl8EbTI/UoY33J0Dp9I/AAAAAAAAAAA/fbchARjKIDg/s500-c/grey.gif"
