function stripHtmlTags(s,max){return s.replace(/<.*?>/ig, '').split(/\s+/).slice(0,max-1).join(' ')}
function createSummaryAndThumb(pID, title, url, date, comment, author, tag, authurl) {
    var posturl = url;
    var authorurl = authurl;
    var title = title;
    var date = date;
    var comment = comment;
    var tag = tag;
    var div = document.getElementById(pID);
    var img = div.getElementsByTagName("img");
    var content1 = div.innerHTML.replace(/<img.*?>/ig, '').replace(/<iframe.*?>/ig, '');
    var arr = content1.split(/<br\s*\/?>/);
    var content = arr[0] + arr.slice(1, -1).join('<br>') + arr.slice(-1);
    var month = new Array();
    month[0] = "Jan";
    month[1] = "Feb";
    month[2] = "Mar";
    month[3] = "Apr";
    month[4] = "May";
    month[5] = "Jun";
    month[6] = "Jul";
    month[7] = "Aug";
    month[8] = "Sep";
    month[9] = "Oct";
    month[10] = "Nov";
    month[11] = "Dec";
    var n = month[date.split('/')[0]];
    var date1 = date.split('/')[1];
    var year = date.split('/')[2];
    var thumb1;
    if (img.length == 1) {
        var imgurl = img[0].src;
        var thumb = '<div class="post-image"><a href="' + posturl + '"><img class="thumb" width="940" height="987" src="' + imgurl + '"></a></div>';
        var summary1 = '<div class="embedd"><header class="post-header"><h2 class="entry-title"><a href=' + posturl + '>' + title + '</a></h2></header><div class="labelhome">In: ' + tag + '</div><div class="cen"><div class="post-details"><span class="post-da"> &nbsp;' + date + '</span><span class="auth"> by&nbsp;<a href=' + authorurl + '>' + author + '</a></span> </div></div></div>' + thumb + '<div class="entry-content clear"><div class="intro-text"><p>' + stripHtmlTags(content, 35) + '</p> </div><div class="post-footer"><div class="pull-left"><a class="read-more" href="' + posturl + '">Read More</a></div><div class="pull-right"></div></div></div>'
    } else {
        if (img.length > 1) {
            if (img.length < 5) {
                var thum = '';
                for (var i = 0; i < img.length; i++) {
                    thum = thum + '<a class="colorboxEx" href="' + img[i].src + '"><img src="' + img[i].src + '"></a>'
                }
                var thumb = '<div class="colorboxEx">' + thum + '</div>';
                var summary1 = '<div class="embedd"><header class="post-header"><h2 class="entry-title"><a href=' + posturl + '>' + title + '</a></h2></header><div class="labelhome">In: ' + tag + '</div><div class="cen"><div class="post-details"><span class="post-da"> &nbsp;' + date + '</span><span class="auth"> by&nbsp;<a href=' + authorurl + '>' + author + '</a></span> </div></div></div>' + thumb + '<div class="entry-content clear"><div class="intro-text"><p>' + stripHtmlTags(content, 35) + '</p> </div><div class="post-footer"><div class="pull-left"><a class="read-more" href="' + posturl + '">Read More</a></div><div class="pull-right"></div></div></div>'
            } else if (img.length > 4) {
                var thum = '';
                for (var i = 0; i < img.length; i++) {
                    thum = thum + '<div><img src="' + img[i].src + '"></div>'
                }
                var thumb = '<div class="slickslider">' + thum + '</div>';
                var summary1 = '<div class="embedd"><header class="post-header"><h2 class="entry-title"><a href=' + posturl + '>' + title + '</a></h2></header><div class="labelhome">In: ' + tag + '</div><div class="cen"><div class="post-details"><span class="post-da"> &nbsp;' + date + '</span><span class="auth"> by&nbsp;<a href=' + authorurl + '>' + author + '</a></span> </div></div></div>' + thumb + '<div class="entry-content clear"><div class="intro-text"><p>' + stripHtmlTags(content, 35) + '</p> </div><div class="post-footer"><div class="pull-left"><a class="read-more" href="' + posturl + '">Read More</a></div><div class="pull-right"></div></div></div>'
            }
        } else {
            var frame = div.getElementsByTagName("iframe");
            if (frame.length >= 1) {
