function getVideoUrl(contentPost) {
    var webDiv = document.createElement('div');
    webDiv.innerHTML = contentPost;
    var iframe = webDiv.getElementsByTagName("iframe");
    if (iframe.length >= 1) {
        video_url = iframe[0].src;
        return video_url
    } else {
        return false
    }
}



function getImageUrl(contentPost) {
    var webDiv = document.createElement('div');
    webDiv.innerHTML = contentPost;
    var iframe = webDiv.getElementsByTagName("iframe");
    if (iframe.length >= 1) {
        video_url = iframe[0].src;
        var videokey = "";
        var vimeokey = "";
        var re = /(\?v=|\/\d\/|\/embed\/)([a-zA-Z0-9\-\_]+)/;
        var redaily = /(\/embed\/video\/)([a-zA-Z0-9\-\_]+)/;
        var vimeo = /player\.vimeo\.com\/video\/([0-9]*)/;
        if (video_url) {
            videokeynum = video_url.match(re);
            videokeydaily = video_url.match(redaily);
            videovimeo = video_url.match(vimeo);
            if (videokeydaily) {
                videokey = video_url.match(redaily)[2];
                imageurl = 'http://res.cloudinary.com/demo/image/dailymotion/w_795,h_440,c_fill/' + videokey + '.jpg'
            } else if (videokeynum) {
                videokey = video_url.match(re)[2];
                imageurl = 'http://res.cloudinary.com/demo/image/youtube/w_795,h_440,c_fill/' + videokey + '.jpg'
            } else if (videovimeo) {
                videokey = videovimeo[1];
                imageurl = 'http://res.cloudinary.com/demo/image/vimeo/w_795,h_440,c_fill/' + videokey + '.jpg'
            }
            if (imageurl) {
                imageurl = imageurl
            } else {
                var img = div.getElementsByTagName("img");
                if (img.length >= 1) {
                    imageurl = img[0].src
                } else {
                    imageurl = "http://2.bp.blogspot.com/-wW571IpilKQ/Usejw7uhkNI/AAAAAAAAAAs/fVjf-GTV9Qk/s1600/imagedf.png"
