    jQuery(window).scroll(function () {
        if (jQuery(this).scrollTop() > 1) {
            jQuery('#back-to-top').fadeIn(250);
        } else {
            jQuery('#back-to-top').fadeOut(250);
        }
    });
    jQuery('#back-to-top').click(function () {
        jQuery('html, body').animate({scrollTop: '0px'}, 800);
        return false;
    });

