function summary(h) {
    summary.count = summary.count || 0;
    summary.count++;
    var g = location.href.indexOf("/search/label/") == -1 && location.href.indexOf("/search?") == -1,
        c = location.href.indexOf("/search/label/") != -1;
    if (summary.count <= alistarbotSummary.skipper) {
        return
    }
    if (g && !alistarbotSummary.DisplayHome) {
        return
    }
    if (c && !alistarbotSummary.DisplayLabel) {
        return
    }
    var e = document.getElementById(h),
        f = e.getElementsByTagName("img");
    if (alistarbotSummary.displayimages) {
        var a = '<img width="' + alistarbotSummary.Widthimg + '" height="' + alistarbotSummary.Heightimg + '"';
        if (alistarbotSummary.imagePosition != "no") {
            var b = alistarbotSummary.imagePosition == "left" ? ' style="float:left;padding:0 5px 5px 0"' : ' style="float:right;padding:0 0 5px 5px"';
            a += b
        }
        a += ' src="' + (f.length > 0 ? f[0].src : alistarbotSummary.noThumb) + '" /><div class="contentos">';
        var d = summary.strip(e.innerHTML, alistarbotSummary.SummaryWords)
    } else {
        var a = "",
            d = summary.strip(e.innerHTML, alistarbotSummary.wordsNoImg)
    }
    e.innerHTML = "<div id='mcontent'>" + a + d + "</div>"
}
summary.strip = function(a, b) {
    return a.replace(/<.*?>/ig, "").split(/\s+/).slice(0, b - 1).join(" ")
};

