jQuery.easing.jswing = jQuery.easing.swing;
jQuery.extend(jQuery.easing, {
    def: "easeOutQuad",
    swing: function(e, f, a, h, g) {
        return jQuery.easing[jQuery.easing.def](e, f, a, h, g)
    },
    easeInQuad: function(e, f, a, h, g) {
        return h * (f /= g) * f + a
    },
    easeOutQuad: function(e, f, a, h, g) {
        return -h * (f /= g) * (f - 2) + a
    },
    easeInOutQuad: function(e, f, a, h, g) {
        if ((f /= g / 2) < 1) {
            return h / 2 * f * f + a
        }
        return -h / 2 * ((--f) * (f - 2) - 1) + a
    },
    easeInCubic: function(e, f, a, h, g) {
        return h * (f /= g) * f * f + a
    },
    easeOutCubic: function(e, f, a, h, g) {
        return h * ((f = f / g - 1) * f * f + 1) + a
    },
    easeInOutCubic: function(e, f, a, h, g) {
        if ((f /= g / 2) < 1) {
            return h / 2 * f * f * f + a
        }
        return h / 2 * ((f -= 2) * f * f + 2) + a
    },
    easeInQuart: function(e, f, a, h, g) {
        return h * (f /= g) * f * f * f + a
    },
    easeOutQuart: function(e, f, a, h, g) {
        return -h * ((f = f / g - 1) * f * f * f - 1) + a
    },
    easeInOutQuart: function(e, f, a, h, g) {
        if ((f /= g / 2) < 1) {
            return h / 2 * f * f * f * f + a
        }
        return -h / 2 * ((f -= 2) * f * f * f - 2) + a
    },
    easeInQuint: function(e, f, a, h, g) {
        return h * (f /= g) * f * f * f * f + a
    },
    easeOutQuint: function(e, f, a, h, g) {
        return h * ((f = f / g - 1) * f * f * f * f + 1) + a
    },
    easeInOutQuint: function(e, f, a, h, g) {
        if ((f /= g / 2) < 1) {
            return h / 2 * f * f * f * f * f + a
        }
        return h / 2 * ((f -= 2) * f * f * f * f + 2) + a
    },
    easeInSine: function(e, f, a, h, g) {
        return -h * Math.cos(f / g * (Math.PI / 2)) + h + a
    },
    easeOutSine: function(e, f, a, h, g) {
        return h * Math.sin(f / g * (Math.PI / 2)) + a
    },
    easeInOutSine: function(e, f, a, h, g) {
        return -h / 2 * (Math.cos(Math.PI * f / g) - 1) + a
    },
    easeInExpo: function(e, f, a, h, g) {
        return (f == 0) ? a : h * Math.pow(2, 10 * (f / g - 1)) + a
    },
    easeOutExpo: function(e, f, a, h, g) {
        return (f == g) ? a + h : h * (-Math.pow(2, -10 * f / g) + 1) + a
    },
    easeInOutExpo: function(e, f, a, h, g) {
        if (f == 0) {
            return a
        }
        if (f == g) {
            return a + h
        }
        if ((f /= g / 2) < 1) {
            return h / 2 * Math.pow(2, 10 * (f - 1)) + a
        }
        return h / 2 * (-Math.pow(2, -10 * --f) + 2) + a
    },
    easeInCirc: function(e, f, a, h, g) {
        return -h * (Math.sqrt(1 - (f /= g) * f) - 1) + a
    },
    easeOutCirc: function(e, f, a, h, g) {
        return h * Math.sqrt(1 - (f = f / g - 1) * f) + a
    },
    easeInOutCirc: function(e, f, a, h, g) {
        if ((f /= g / 2) < 1) {
            return -h / 2 * (Math.sqrt(1 - f * f) - 1) + a
        }
        return h / 2 * (Math.sqrt(1 - (f -= 2) * f) + 1) + a
    },
    easeInElastic: function(l, j, r, k, h) {
        var f = 1.70158;
        var g = 0;
        var e = k;
        if (j == 0) {
            return r
        }
        if ((j /= h) == 1) {
            return r + k
        }
        if (!g) {
            g = h * 0.3
        }
        if (e < Math.abs(k)) {
            e = k;
            var f = g / 4
        } else {
            var f = g / (2 * Math.PI) * Math.asin(k / e)
        }
        return -(e * Math.pow(2, 10 * (j -= 1)) * Math.sin((j * h - f) * (2 * Math.PI) / g)) + r
    },
    easeOutElastic: function(l, j, r, k, h) {
        var f = 1.70158;
        var g = 0;
        var e = k;
        if (j == 0) {
            return r
        }
        if ((j /= h) == 1) {
            return r + k
        }
        if (!g) {
            g = h * 0.3
        }
        if (e < Math.abs(k)) {
            e = k;
            var f = g / 4
        } else {
            var f = g / (2 * Math.PI) * Math.asin(k / e)
        }
        return e * Math.pow(2, -10 * j) * Math.sin((j * h - f) * (2 * Math.PI) / g) + k + r
    },
    easeInOutElastic: function(l, j, r, k, h) {
        var f = 1.70158;
        var g = 0;
        var e = k;
        if (j == 0) {
            return r
        }
        if ((j /= h / 2) == 2) {
            return r + k
        }
        if (!g) {
            g = h * (0.3 * 1.5)
        }
        if (e < Math.abs(k)) {
            e = k;
            var f = g / 4
        } else {
            var f = g / (2 * Math.PI) * Math.asin(k / e)
        }
        if (j < 1) {
            return -0.5 * (e * Math.pow(2, 10 * (j -= 1)) * Math.sin((j * h - f) * (2 * Math.PI) / g)) + r
        }
        return e * Math.pow(2, -10 * (j -= 1)) * Math.sin((j * h - f) * (2 * Math.PI) / g) * 0.5 + k + r
    },
    easeInBack: function(e, f, a, n, h, g) {
        if (g == undefined) {
            g = 1.70158
        }
        return n * (f /= h) * f * ((g + 1) * f - g) + a
    },
    easeOutBack: function(e, f, a, n, h, g) {
        if (g == undefined) {
            g = 1.70158
        }
        return n * ((f = f / h - 1) * f * ((g + 1) * f + g) + 1) + a
    },
    easeInOutBack: function(e, f, a, n, h, g) {
        if (g == undefined) {
            g = 1.70158
        }
        if ((f /= h / 2) < 1) {
            return n / 2 * (f * f * (((g *= (1.525)) + 1) * f - g)) + a
        }
        return n / 2 * ((f -= 2) * f * (((g *= (1.525)) + 1) * f + g) + 2) + a
    },
    easeInBounce: function(e, f, a, h, g) {
        return h - jQuery.easing.easeOutBounce(e, g - f, 0, h, g) + a
    },
    easeOutBounce: function(e, f, a, h, g) {
        if ((f /= g) < (1 / 2.75)) {
            return h * (7.5625 * f * f) + a
        } else {
            if (f < (2 / 2.75)) {
                return h * (7.5625 * (f -= (1.5 / 2.75)) * f + 0.75) + a
            } else {
                if (f < (2.5 / 2.75)) {
                    return h * (7.5625 * (f -= (2.25 / 2.75)) * f + 0.9375) + a
                } else {
                    return h * (7.5625 * (f -= (2.625 / 2.75)) * f + 0.984375) + a
                }
            }
        }
    },
    easeInOutBounce: function(e, f, a, h, g) {
        if (f < g / 2) {
            return jQuery.easing.easeInBounce(e, f * 2, 0, h, g) * 0.5 + a
        }
        return jQuery.easing.easeOutBounce(e, f * 2 - g, 0, h, g) * 0.5 + h * 0.5 + a
    }
});
(function(a) {
    a.fn.hoverTimeout = function(d, j, b, e) {
        return this.each(function() {
            var f = null,
                g = a(this);
            g.hover(function() {
                clearTimeout(f);
                f = setTimeout(function() {
                    j.call(g)
                }, d)
            }, function() {
                clearTimeout(f);
                f = setTimeout(function() {
                    e.call(g)
                }, b)
            })
        })
    }
})(jQuery);



function PostSlide(b) {
    (function(a) {
        var f = {
            blogURL: "",
            MaxPost: 4,
            idcontaint: "",
            ImageSize: 500,
            Summarylength: 150,
            RandompostActive: true,
            loadingClass: "loadingz",
            pBlank: "",
            MonthNames: ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"],
            tagName: false
        };
        f = a.extend({}, f, b);
        var g = a(f.idcontaint);
        g.html('<div class="my_slider_wrap"><ul class="slides"></ul></div>').addClass(f.loadingClass);
        var h = function(D) {
            var z, B, x, e, F, C, G, j, E, y, A = "",
                w = D.feed.entry;
            for (var H = 0; H < w.length; H++) {
                for (var d = 0; d < w[H].link.length; d++) {
                    if (w[H].link[d].rel == "alternate") {
                        z = w[H].link[d].href;
                        break
                    }
                }
                if ("media$thumbnail" in w[H]) {
                    F = w[H].media$thumbnail.url.replace(/\/s[0-9]+\-c/g, "/s" + f.ImageSize);
                    if (w[H].media$thumbnail.url.indexOf("img.youtube.com") != -1) {
                        F = w[H].media$thumbnail.url.replace("default", "0")
                    }
                } else {
                    F = f.pBlank.replace(/\/s[0-9]+\-c/g, "/s" + f.ImageSize)
                }
                if ("content" in w[H]) {
                    x = w[H].content.$t
                } else {
                    if ("summary" in w[H]) {
                        x = w[H].summary.$t
                    } else {
                        x = ""
                    }
                }
                x = x.replace(/<\S[^>]*>/g, "");
                if (x.length > f.Summarylength) {
                    x = x.substring(0, f.Summarylength) + "..."
                }
                B = w[H].title.$t;
                y = w[H].published.$t.substring(0, 10);
                C = y.substring(0, 4);
                G = y.substring(5, 7);
                j = y.substring(8, 10);
                E = f.MonthNames[parseInt(G, 10) - 1];
                A += '<li class="my_slider_item"><a title="' + B + '" class="" href="' + z + '"><img src="' + F + '"/></a><div class="meta-tag"></div><div class="post-caption-content"><span class="post-meta"><span class="dd">' + j + '</span> <span class="dm">' + E + '</span> <span class="dy">' + C + '</span></span><div class="slider_caption"><h3><a href="' + z + '">' + B + '</a></h3></div><div class="post-readmore"><a class="btn-link" href="' + z + '" rel="bookmark">Read more</a></div></div></li>'
            }
            g.find("ul.slides").append(A);
            if (!f.RandompostActive) {
                a(f.idcontaint + " .my_slider_wrap").flexslider({
                    animation: "fade",
                    selector: ".slides > li",
                    animationLoop: true,
                    smoothHeight: true,
                    pauseOnHover: true,
                    mousewheel: false,
                });
                g.removeClass(f.loadingClass)
            }
        };
        a.get((f.blogURL === "" ? window.location.protocol + "//" + window.location.host : f.blogURL) + "/feeds/posts/summary" + (f.tagName === false ? "" : "/-/" + f.tagName) + "?max-results=0&orderby=published&alt=json-in-script", function(e) {
            Total_Posts_Number = e.feed.openSearch$totalResults.$t;
            if (Total_Posts_Number <= f.MaxPost) {
                f.MaxPost = Total_Posts_Number
            }
            var r = [];
            while (r.length < f.MaxPost) {
                var p = Math.ceil(Math.random() * Total_Posts_Number);
                var d = false;
                for (var k = 0; k < r.length; k++) {
                    if (r[k] == p) {
                        d = true;
                        break
                    }
                }
                if (!d) {
                    r[r.length] = p
                }
            }
            if (f.RandompostActive == true) {
                var j;
                for (var o = 0; o < f.MaxPost; o++) {
                    j = a.get((f.blogURL === "" ? window.location.protocol + "//" + window.location.host : f.blogURL) + "/feeds/posts/default" + (f.tagName === false ? "" : "/-/" + f.tagName) + "?start-index=" + r[o] + "&max-results=1&orderby=published&alt=json-in-script", h, "jsonp")
                }
                a.when(j).done(function() {
                    a(f.idcontaint + " .my_slider_wrap").flexslider({
                        animation: "fade",
                        selector: ".slides > li",
                        animationLoop: true,
                        smoothHeight: true,
                        pauseOnHover: true,
                        mousewheel: false,
                        initDelay: f.MaxPost * 1700,
                    });
                    g.removeClass(f.loadingClass)
                })
            } else {
                a.get((f.blogURL === "" ? window.location.protocol + "//" + window.location.host : f.blogURL) + "/feeds/posts/default" + (f.tagName === false ? "" : "/-/" + f.tagName) + "?max-results=" + f.MaxPost + "&orderby=published&alt=json-in-script", h, "jsonp")
            }
        }, "jsonp")
    })(jQuery)
};




