var summary1 = '<div class="unimage">' + imgtag + '</div><div class="my-post"><div class="my-post-holder"><div class="my-post-inner"><h2 class="home-title"><a href="' + y + '">' + x + '</a></h2><div class="bt-sept"></div><div class="oi_port_cats"></div><div class="oi_small_descr"><p>' + stripTags(p['innerHTML'], 25) + '...</p></div><a href="' + y + '" class="button button--ujarak button--border-thin button--text-thick">Read More</a></div></div></div>';

