var classicMode = false;
var summary = 20;
var indent = 3;
imgr = new Array();
imgr[0] = "http://sites.google.com/site/fdblogsite/Home/nothumbnail.gif";
showRandomImg = true;
aBold = true;
summaryPost = 170;
summaryTitle = 25;
numposts1 = 6;
numposts2 = 8;
numposts3=3;
