if ($('body')['hasClass']('has-load-animation')) {
    var CssLoadAnimation = '<div id="loading">' + '<div class="cssload-thecube">' + '<div class="cssload-cube cssload-c1"></div>' + '<div class="cssload-cube cssload-c2"></div>' + '<div class="cssload-cube cssload-c4"></div>' + '<div class="cssload-cube cssload-c3"></div>' + '</div>' + '</div>',
        DocToLoad = 'body';
    $(DocToLoad)['prepend'](CssLoadAnimation);
    $('html')['css']('overflow', 'hidden');
    $(window)['load'](function() {
        $('#loading')['fadeOut']('slow');
        $('html')['css']('overflow', 'auto')
    })
};


$(document).ready(function() {


