if ($('#featured-posts-slideshow')['length']) {
    var OwlSlideshowTime = 7;
    var $OwlProgressBar, $OwlBar, $OwlElem, OwlisPause, Owltick, OwlPercentTime;

    function OwlProgressBar(_0x7b63x9) {
        $OwlElem = _0x7b63x9;
        OwlBuildProgressBar();
        OwlStart()
    }

    function OwlBuildProgressBar() {
        $OwlProgressBar = $('<div>', {
            id: 'owl-progress-bar'
        });
        $OwlBar = $('<div>', {
            id: 'owl-bar'
        });
        $OwlProgressBar['append']($OwlBar)['prependTo']($OwlElem)
    }

    function OwlStart() {
        OwlPercentTime = 0;
        OwlisPause = false;
        Owltick = setInterval(interval, 10)
    }

    function interval() {
        if (OwlisPause === false) {
            OwlPercentTime += 1 / OwlSlideshowTime;
            $OwlBar['css']({
                width: OwlPercentTime + '%'
            });
            if (OwlPercentTime >= 100) {
                $OwlElem['trigger']('owl.next')
            }
        }
    }

    function OwlPauseOnDragging() {
        OwlisPause = true
    }

    function OwlMoved() {
        clearTimeout(Owltick);
        OwlStart()
    }
    $('#featured-posts-slideshow')['owlCarousel']({
        singleItem: true,
        afterInit: OwlProgressBar,
        afterMove: OwlMoved,
        startDragging: OwlPauseOnDragging,
        navigation: true,
        pagination: true,
        slideSpeed: 700,
        paginationSpeed: 700,
        transitionStyle: 'fade',
        navigationText: ['<i class=\'fa fa-angle-left\'></i>', '<i class=\'fa fa-angle-right\'></i>']
    });
    $OwlElem['on']('mouseover', function() {
        OwlisPause = true
    });
    $OwlElem['on']('mouseout', function() {
        OwlisPause = false
    })
