    maxresults = 6,
    splittercolor = "#d4eaf2",
    relatedpoststitle = "",
    relatedTitles = [],
    relatedTitlesNum = 0,
    relatedUrls = [],
    thumburl = [];

function related_results_labels_thumbs(f) {
    for (var e = 0; e < f.feed.entry.length; e++) {
        var g = f.feed.entry[e];
        relatedTitles[relatedTitlesNum] = g.title.$t;
        try {
            thumburl[relatedTitlesNum] = g.media$thumbnail.url
        } catch (k) {
            s = g.content.$t, a = s.indexOf("<img"), b = s.indexOf('src="', a), c = s.indexOf('"', b + 5), d = s.substr(b + 5, c - b - 5), thumburl[relatedTitlesNum] = -1 != a && -1 != b && -1 != c && "" != d ? d : "undefined" !== typeof defaultnoimage ? defaultnoimage : "http://2.bp.blogspot.com/-QP4NqoWBg8Y/TZynybflhKI/AAAAAAAAAvM/7MRErFNCiFE/s1600/No-Image.jpg"
        }
        70 < relatedTitles[relatedTitlesNum].length && (relatedTitles[relatedTitlesNum] = relatedTitles[relatedTitlesNum].substring(0, 70) + "...");
        for (var h = 0; h < g.link.length; h++) "alternate" == g.link[h].rel && (relatedUrls[relatedTitlesNum] = g.link[h].href, relatedTitlesNum++)
    }
}

function removeRelatedDuplicates_thumbs() {
    for (var f = [], e = [], g = [], h = 0; h < relatedUrls.length; h++) contains_thumbs(f, relatedUrls[h]) || (f.length += 1, f[f.length - 1] = relatedUrls[h], e.length += 1, g.length += 1, e[e.length - 1] = relatedTitles[h], g[g.length - 1] = thumburl[h]);
    relatedTitles = e;
    relatedUrls = f;
    thumburl = g
}

function contains_thumbs(f, e) {
    for (var g = 0; g < f.length; g++)
        if (f[g] == e) return !0;
    return !1
}

function printRelatedLabels_thumbs(f) {
    for (var e = 0; e < relatedUrls.length; e++) relatedUrls[e] != f && relatedTitles[e] || (relatedUrls.splice(e, 1), relatedTitles.splice(e, 1), thumburl.splice(e, 1), e--);
    f = Math.floor((relatedTitles.length - 1) * Math.random());
    e = 0;
    0 < relatedTitles.length && document.write("<h2>" + relatedpoststitle + "</h2>");
    for (document.write("<div/>"); e < relatedTitles.length && 30 > e && e < maxresults;) document.write('<div class="recent-item"><div class="post-thumbnail"><a class="arecent" href="' + relatedUrls[f] + '"><img  class="img-related" src="' + thumburl[f].replace(/\/s[0-9]+\-c/g, "/w222-h150-c") + '"/></a></div><h3 class="metatag"><a href="' + relatedUrls[f] + '">' + relatedTitles[f] + "</a></h3></div>"), e++, f < relatedTitles.length - 1 ? f++ : f = 0;
    document.write("</div>");
    relatedUrls.splice(0, relatedUrls.length);
    thumburl.splice(0, thumburl.length);
    relatedTitles.splice(0, relatedTitles.length)
}
